import { useState } from 'react';
import classes from './Available.module.css';
import MealItem from '../Meals/MealItem/MealItem';
import Card from '../UI/Card';
import AddMealForm from './AvalableMealsForm'; // Import the AddMealForm component

const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'PoLO shirt',
    description: 'Cotton made',
    price: 22.99,
    size: 'M'
  },
  {
    id: 'm2',
    name: 'Jeans',
    description: 'pure jeans',
    price: 16.5,
    size: 'M'
  },
  {
    id: 'm3',
    name: 'Armani shirt',
    description: 'comfortable',
    price: 12.99,
    size: 'M'
  },
  {
    id: 'm4',
    name: 'New trends short',
    description: 'Cotton Polyester',
    price: 18.99,
    size: 'M'
  },
];

const AvailableMeals = () => {
  const [meals, setMeals] = useState(DUMMY_MEALS);

  const addMealHandler = (newMeal) => {
    setMeals((prevMeals) => [...prevMeals, newMeal]);
  };

  const mealsList = meals.map((meal) => (
    <tr key={meal.id}>
      <td>{meal.name}</td>
      <td>{meal.description}</td>
      <td>{meal.price}</td>
      <td>{meal.size}</td>
    </tr>
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <AddMealForm onAddMeal={addMealHandler} /> {/* Add the AddMealForm component */}
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Price</th>
              <th>Size</th>
            </tr>
          </thead>
          <tbody>{mealsList}</tbody>
        </table>
      </Card>
    </section>
  );
};

export default AvailableMeals;
