import { useState } from 'react';
import classes from './Available.module.css';
import MealItem from '../Meals/MealItem/MealItem';
import Card from '../UI/Card';
import AddMealForm from './AvalableMealsForm'; // Import the AddMealForm component

const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'PoLO shirt',
    description: 'Cotton made',
    price: 22.99,
    size: 'M'
  },
  {
    id: 'm2',
    name: 'Jeens',
    description: 'pure jeens',
    price: 16.5,
    size: 'M'
  },
 
  {
    id: 'm4',
    name:'denim shirt', 
    description:'Pure Luxury',
    price :	'$20.00	',
    size:'L'
  },
];

const AvailableMeals = () => {
  const [meals, setMeals] = useState(DUMMY_MEALS);

  const addMealHandler = (newMeal) => {
    setMeals((prevMeals) => [...prevMeals, newMeal]);
  };

  const mealsList = meals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
      size={meal.size}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <AddMealForm onAddMeal={addMealHandler} /> {/* Add the AddMealForm component */}
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
